    <!-- Finance- Pop Up Modal Start -->
    <section id="myModal" class="modal">
      <div class="modal-content">
        <div id="popup-modal">
          <form action="#" method="post" id="signup">
            <div class="modal-title">
              <h1>Create Modal</h1>
              <span class="close-btn close">
                <i class="fa-solid fa-xmark"></i>
              </span>
            </div>
            <div class="row">
              <div class="col-lg-6">
                <label class="data">
                  <input
                    type="text"
                    name="firstName"
                    placeholder="Enter your full name"
                    required
                  /><br />
                </label>

                <label class="data">
                  <input
                    type="email"
                    name="email"
                    placeholder="Enter your email"
                    required
                  /><br />
                </label>

                <label class="data">
                  <input
                    type="text"
                    name="Username"
                    placeholder="Enter your email or username"
                    required
                  /><br />
                </label>

                <label class="country">
                  <select name="country">
                    <option>Select Your Country</option>
                    <option>Bangladesh</option>
                    <option>Soudi Arab</option>
                    <option>America</option>
                    <option>Brazil</option></select
                  ><br />
                </label>

                <div class="datepicker-wrapper">
                  <lavel class="input-header">Choose a Date</lavel>
                  <div class="controls datepick_wrap">
                    <input
                      id="datepicks"
                      class="datepicker form-control"
                      placeholder="Select date"
                      type="text"
                    />
                    <label class="calander-icon" for="datepicks"
                      ><img src="./assets/icons/calender.svg" alt=""
                    /></label>
                  </div>
                </div>

                <fieldset class="radioOrCheckboxes">
                  <lavel class="input-header">Programming Skills</lavel>
                  <div class="skills">
                    <div class="skil-item">
                      <label>
                        <input type="checkbox" name="hobbies" value="music" />
                        HTML
                      </label>
                      <label>
                        <input type="checkbox" name="travel" value="travel" />
                        CSS
                      </label>
                      <label>
                        <input type="checkbox" name="movies" value="movies" />
                        JavaScript
                      </label>
                    </div>
                    <div class="skill-item">
                      <label>
                        <input type="checkbox" name="cooking" value="cooking" />
                        React
                      </label>
                      <label>
                        <input type="checkbox" name="movies" value="movies" />
                        Angular
                      </label>
                    </div>
                  </div>
                </fieldset>
              </div>

              <div class="col-lg-6">
                <label class="data">
                  <input
                    type="number"
                    name="secondName"
                    placeholder="Enter your phone number"
                    required
                  />
                </label>

                <label class="data">
                  <input
                    type="password"
                    name="password"
                    placeholder="Enter your password"
                    required
                  /><br />
                </label>

                <fieldset class="radioOrCheckboxes">
                  <label class="input-header">Gender</label>
                  <div class="gender">
                    <label class="male">
                      <input type="radio" name="gender" value="male" />
                      <span>Male </span>
                    </label>
                    <label class="female">
                      <input type="radio" name="gender" value="female" />
                      <span>Female</span>
                    </label>
                  </div>
                </fieldset>

                <label class="data textarea">
                  <textarea
                    name="bio"
                    rows="6"
                    placeholder="Type Your Messages..."
                  ></textarea
                  ><br />
                </label>

                <div class="upload-profile">
                  <div class="item">
                    <div class="img-box">
                      <svg
                        width="32"
                        height="32"
                        viewBox="0 0 50 50"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                        xmlns:xlink="http://www.w3.org/1999/xlink"
                      >
                        <rect
                          width="50"
                          height="50"
                          fill="url(#pattern0_1204_6)"
                          fill-opacity="0.5"
                        />
                        <defs>
                          <pattern
                            id="pattern0_1204_6"
                            patternContentUnits="objectBoundingBox"
                            width="1"
                            height="1"
                          >
                            <use
                              xlink:href="#image0_1204_6"
                              transform="scale(0.005)"
                            />
                          </pattern>
                          <image
                            id="image0_1204_6"
                            width="200"
                            height="200"
                            xlink:href="data:image/png;base64,"
                          />
                        </defs>
                      </svg>
                    </div>

                    <div class="profile-wrapper">
                      <label class="custom-file-input-wrapper">
                        <input
                          type="file"
                          class="custom-file-input"
                          aria-label="Upload Photo"
                        />
                      </label>
                      <p>PNG,JPEG or GIF (up to 1 MB)</p>
                    </div>
                  </div>
                  <button type="submit" class="submit">Submit</button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </section>
    <!-- Finance- Pop Up Modal End -->
